const join2Array = (arr1, arr2) => {
	const res = [];

	arr1.forEach(
    el1 => {
      arr2.forEach(
        el2 => {
          res.push(`${el1} ${el2}`)
        }
      )
    }
  );

  return res;
}

const joinMultiArray = multiArray => {
	if (1 < multiArray.length){
  	const arr1 = multiArray.shift();
    const arr2 = multiArray.shift();
    const newArr = join2Array(arr1, arr2);
    multiArray.unshift(newArr);
    joinMultiArray(multiArray);
  }

  return multiArray[0];
}

const result = joinMultiArray([...arguments]);

